    public static function determineFloorNumber($input) {
        
        $input_array = str_split($input);

        $sum = 0;
        for ($i = 0; $i < count($input_arry); $i++) {
            if ($input_array[$i] == "(") {
                $sum++;
            } elseif ($input_array[$i] == ")") {
                $sum--;
            }
        }
        return $sum;
    }

    public static function determineWhenEnteringBasement($input) {
        
        $input_array = str_split($input);

        $sum = 0;
        $count = count($input_array);
        for ($i = 0; $i < $count; $i++) {
            if ($input_array[$i] == "(") {
                $sum++;
            } elseif ($input_array[$i] == ")") {
                $sum--;
            }

            if ($sum < 0) {
                return $i+1;
            }
        }
        return $sum;
    }

